const breakpoints = [
  `640px`, // 40rem
  `896px`, // 56rem
  `1280px`, // 80rem
]

const baseColors = {
  primary: '#8592e0',
  primaryDark: '#16745f',
  primaryDarker: '#0d5e4c',
  secondary: '#f4f0ec',
  grey0: '#000000',
  grey20: '#333333',
  grey90: '#e6e6e6',
  grey53: '#878787',
  white: '#fff',
}

const headings = {
  level: {
    1: {
      fontSize: [
        '1.625rem', // 26px
        '2rem', // 32px
        '2.5rem', // 40px
        '2.75rem', // 44px
      ],
      lineHeight: 1.3,
    },
    4: {
      lineHeight: 1.2,
    },
  },
}

export default {
  background: {
    default: {
      bg: 'white',
      color: 'dark',
    },
    dark: {
      bg: 'dark',
      color: 'white',
    },
    secondary: {
      bg: 'secondary',
      color: 'dark',
    },
  },
  breakpoints,
  buttons: {
    default: {
      bg: 'gray',
      color: 'white',
      '&:hover': {
        backgroundColor: 'primaryDark',
      },
    },
    primary: {
      bg: 'primaryDark',
      color: 'white',
      '&:hover': {
        backgroundColor: 'primaryDarker',
      },
    },
    secondary: {
      bg: 'grey0',
      color: 'white',
      '&:hover': {
        backgroundColor: 'grey20',
      },
    },
    light: {
      bg: 'white',
      color: 'grey0',
      border: '1px solid',
      borderColor: 'grey0',
      '&:hover': {
        backgroundColor: 'grey90',
      },
    },
  },
  slider: {
    arrow: {
      background: 'transparent',
      border: '2px dotted transparent',
      color: 'dark',
      '&:focus': {
        borderColor: 'dark',
      },
      '&:hover': {
        color: 'border',
      },
    },
  },
  colors: {
    ...baseColors,
    textGrey: baseColors.grey53,
    transparent: 'transparent',
    dark: baseColors.grey0,
    text: baseColors.grey20,
    background: baseColors.white,
    body: baseColors.grey0,
    border: '#ccc',
    modes: {
      dark: {
        background: baseColors.grey0,
        text: baseColors.white,
      },
    },
  },
  container: {
    inner: {
      py: '8vw',
      px: '2rem',
    },
    grid: {
      width: '100%',
    },
    wrapper: {
      maxWidth: ['90%', null, null, '75%'],
      m: '0 auto',
    },
    eyebrow: {
      color: 'primary',
    },
    description: {
      mr: 'auto',
      ml: 'auto',
      mb: 'hPadding',
      maxWidth: '38rem',
      fontSize: ['kicker', null, null, 'lg'],
      fontWeight: 'lean',
      p: {
        lineHeight: 'desc',
        display: 'inline-block',
      },
      'p:last-of-type': {
        mb: '0',
      },
    },
  },
  fonts: {
    body:
      'system-ui, -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, "Helvetica Neue", sans-serif',
    heading: '"Inter", serif',
    monospace: 'Menlo, monospace',
  },
  fontSizes: {
    tiny: '.875rem',
    base: '0.9rem',
    kicker: '1.15rem',
    lg: '1.4rem',
    xl: '2.75rem',
  },
  fontWeights: {
    lean: '350',
    body: '400',
    normal: '500',
    heading: '600',
  },

  lineHeights: {
    heading: '1.25',
    button: '1.3',
    desc: '1.45',
    body: '1.625',
  },
  radii: {
    button: '4px',
    card: '8px',
  },
  sizes: {
    full: '100%',
  },
  space: {
    // Legacy vuejs scales.
    hPaddingHalf: '1rem',
    hPadding: '2rem',
    cPadding: '1.25rem',
    cMargin: '1rem',

    vMargin: '2.5rem',
    vMargin2x: '5rem',

    vSpace: '8vw',

    // New spaces scale.
    none: 0,
    xxsmall: '0.25rem', // 4px
    xsmall: '0.5rem', // 8px
    small: '1rem', // 16px
    medium: '2rem', // 32px
    large: '4rem', // 64px
    xlarge: '8rem', // 128px
    xxlarge: '16rem', // 256px
    xxxlarge: '32rem', // 512px
  },
  tabs: {
    default: {
      item: {
        '&::after': {
          height: 2,
          left: 0,
        },
      },
    },
    vertical: {
      display: [null, 'flex'],
      label: {
        pt: ['small', 0],
        display: 'block',
        ':focus': {
          outline: '3px solid currentColor',
          zIndex: '1',
        },
      },
      content: {
        width: '100%',
      },
      item: {
        width: '100%',
        mx: 0,
        pr: 'medium',
        borderTop: '1px solid',
        borderColor: 'grey90',
        ':last-of-type:not(.active)': {
          borderBottom: '1px solid',
          borderColor: 'grey90',
        },
      },
    },
  },

  // Default Components

  cards: {
    default: {
      bg: 'secondary',
      color: 'dark',
    },
  },
  styles: {
    root: {
      color: 'text',
      bg: 'background',
      fontSize: 'base',
      fontFamily: 'body',
      lineHeight: 'body',
      fontWeight: 'body',
      m: 0,
      p: 0,
    },
    'h1, h2, h3, h4': {
      mt: 0,
    },
    h1: {
      ...headings.level[1],
    },
    p: {
      fontSize: 'body',
      mt: 0,
      mb: 0,
    },
    img: {
      maxWidth: '100%',
    },
  },
  text: {
    desc: {
      fontSize: 'lg',
      fontWeight: 'lean',
      lineHeight: 'desc',
      mx: 'auto',
      mt: 'vMargin',
      maxWidth: '42rem',
    },
  },
}
import React from 'react'
import PropTypes from 'prop-types'
import { Link, RichText } from 'prismic-reactjs'
import { Box } from 'theme-ui'

function Button({ link, label, linkResolver, ...props }) {
  const buttonStyles = {
    p: '1em 3em',
    textAlign: 'center',
    width: 'auto',
    display: ['block', 'inline-block'],
    fontSize: 'tiny',
    lineHeight: 'button',
    textDecoration: 'none',
    cursor: 'pointer',
    borderRadius: 'button',
    border: 'none',
    transition: 'all 0.1s linear',
    '&:focus': {
      outline: '3px solid currentColor',
      outlineOffset: '3px',
    },
  }

  const elementType = link ? 'a' : 'button'
  const linkProps = link ? { href: Link.url(link, linkResolver) } : {}

  return (
    <Box
      as={elementType}
      variant="default"
      {...props}
      __themeKey="buttons"
      __css={buttonStyles}
      {...linkProps}
    >
      {typeof label === 'string' ? label : <RichText render={label} />}
    </Box>
  )
}

Button.defaultProps = {
  linkResolver: () => {
    return '/404'
  },
  link: null,
  label: null,
}

Button.propTypes = {
  link: PropTypes.shape({}),
  label: PropTypes.string,
  linkResolver: PropTypes.func,
}

export default Button
import React from 'react'
import PropTypes from 'prop-types'
import { Box } from 'theme-ui'

const Card = ({ children, ...props }) => {
  return (
    <Box
      variant="default"
      {...props}
      __css={{
        borderRadius: 'card',
        px: 'small',
        py: 'medium',
        textAlign: 'center',
      }}
      __themeKey="cards"
    >
      {children}
    </Box>
  )
}

Card.propTypes = {
  children: PropTypes.node.isRequired,
}

export default Card
import React from 'react'
import PropTypes from 'prop-types'
import { Box } from 'theme-ui'

const CardContent = ({ children, ...props }) => {
